// Helper function to get group icon by name
export function getGroupIconHex(iconName: string): string | null {
  return groupIconHexMap[iconName] || null;
}

// Helper function to check if an icon is a group icon
export function isGroupIcon(iconName: string): boolean {
  return availableGroupIcons.includes(iconName);
}

// Helper function to get group icons by provider
export function getGroupIconsByProvider(provider: 'aws' | 'gcp' | 'azure'): GroupIcon[] {
  if (provider === 'aws') return awsGroupIcons;
  if (provider === 'gcp') return gcpGroupIcons;
  return azureGroupIcons;
}

